mapper.select:
select a.id,a.asset_code,a.asset_name,a.asset_specifications, a.asset_unit,a.asset_price,a.remark,a.create_user,a.create_time, a.create_way,a.update_user,a.update_time,a.status,ac.classify_name from asset a left join asset_classify ac on a.classify_id = ac.classify_id
a.asset_del = '0'
and a.ssset_code = #{assetCode}
and a.asset_name like concat('%',#{assetName},'%')
and a.asset_specifications like concat('%',#{assetSpecifications},'%')
and a.asset_unit = #{assetUnit}
and a.classify_id = #{classifyId}
and a.create_user like concat('%',#{createUser},'%')
and date_format(a.create_time,'%y%m%d') >= date_format(#{beginCreateTime},'%y%m%d')
and date_format(a.create_time,'%y%m%d') <= date_format(#{endCreateTime},'%y%m%d')
and date_format(a.update_time,'%y%m%d') >= date_format(#{beginUpdateTime},'%y%m%d')
and date_format(a.update_time,'%y%m%d') <= date_format(#{endUpdateTime},'%y%m%d')
and a.create_way = #{createWay}
and a.update_user like concat('%',#{updateUser},'%')
and a.status = #{status}
order by a.update_time desc,a.create_time desc

mapper.update:
update asset
asset_name = #{assetName}, status = #{status}, asset_specifications = #{assetSpecifications}, asset_unit = #{assetUnit}, classify_id = #{classifyId}, asset_price = #{assetPrice}, remark = #{remark}, update_user = #{updateUser}, update_time = #{updateTime}
where id = #{id}
